# Handles avec exactement 5 caractères (sans le @)
five_chars = 0
handles.each do |handle|
  five_chars += 1 if handle.length == 6
end
puts "Handles with exactly 5 characters : #{five_chars}"
puts

# Handles commençant par une majuscule
uppercase = 0
handles.each do |handle|
  #uppercase += 1 if handle[1] == handle[1].upcase 
  #(C'était ma propriété de départ, mais je me suis demandé si il ne prenait que les lettres ou également les signes. 
  #Il prend à priori les signes car je passe de 213 avec cette ligne, à 209 avec ma nouvelle et il y a bien 4 pseudo commençant par "_")
  char = handle[1]
  uppercase += 1 if char >= "A" && char <= "Z"
end
puts "Handles starting with an uppercase letter: #{uppercase}"
puts

# Tri alphabétique
puts "Alphabetical order:"
puts handles.sort
puts
#J'ai discuté avec ChatGPT et il m'a proposé cette solution pour faire un tri plus "humain" (en ne plaçant pas les Maj avant les min)
#handles.sort_by { |handle| handle.downcase }

# Tri par taille
puts "Handles sorted by length:"
puts handles.sort_by { |handle| handle.length }
puts

# Position de @epenser
position = handles.index("@epenser")
puts "@epenser is at position: #{position}"
puts

# Répartition par taille (HASH)
distribution = {}

handles.each do |handle| #(mon p'tit mémo) - Pour chaque handle dans l’array handles, fais ce que je vais mettre dans le bloc 
  size = handle[1..-1].length
  if distribution[size]
    distribution[size] += 1
  else
    distribution[size] = 1
  end
end

puts "Distribution of handles by size:"
distribution.sort.each do |size, count|
  puts "- #{count} handle(s) with #{size} character(s)"
end
